import React, { Component } from 'react';
import './Calculadora.css';
import './Portales.css'
import Boton from './Boton'

var hola = '';
class Portales extends Component {

    state = {
        posX: '',
        posY: ''
    }


    limpiar = () =>{
        console.log(this.state);
    }

    onChange = e => {
        // console.log(e.target.value);
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        return (
            <div id="calculadora">
                <h2 id="calculadora__title">Calculadora</h2>
                <div id="calculadora__form">
                    <div id="form__inputs">
                        <input type="number"
                            name="posX"
                            onChange={this.onChange}
                            value={this.state.posX}
                            id="pos-x"
                            placeholder="Coordenada X" />
                        <input type="number" id="pos-y"
                            name="posX"
                            onChange={this.onChange}
                            value={this.state.posY}
                            placeholder="Coordenada Y" />
                    </div>
                    <div id="btn-limpiar">
                        <Boton className="btn-calc" click={this.limpiar} name="Limpiar"></Boton>

                    </div>
                </div>
                <div id="calculadora__btn">
                    <Boton click={this.limpiar} name="OverWorld  ->  Nether"></Boton>
                    <Boton click={this.limpiar} name="Nether  ->  OverWorld"></Boton>
                    <Boton click={this.limpiar} name="Guardar"></Boton>
                </div>
                <div id="calculadora__res">Resultados</div>
            </div>
        )
    }
}


export default Portales;
