import React, { Component } from 'react'
import './Calculadora.css'
import './RestBlocks.css'
import Button from './Boton'

const errorNumber = {
    borderBottom: "2px solid rgb(177, 0, 0)",
    background: "rgba(177, 146, 146,100)"
}
const validNumber = {
    borderBottom: ("2px solid rgb(0, 92, 69)"),
    background: "transparent"
}

export class RestBlocks extends Component {

    state = {
        posX: '',
        posY: '',
        posZ: '',
        posDestX: '',
        posDestY: '',
        posDestZ: '',
        style: validNumber
    }
    validate = (coord) => {
        for (let element of coord) {
            if (isNaN(element)) {
                alert("Ingresa SOLO numeros\n y no modifiques mi html, Gracias\n atte: Rolgus.")
                return false;
            }
            if (element === "") {
                this.errorValues();
                return false;
            }
        }
        return true;
    }

    limpiar = () => {
        this.setState({
            posX: '',
            posY: '',
            posZ: ''
        });
    }
    onChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }


    render() {
        return (
            <div id="calculadora">
                <div id="calculadora__form">
                    <div>
                        <input type="number"
                            style={this.state.style}
                            name="posX"
                            onChange={this.onChange}
                            value={this.state.posX}
                            id="pos-x"
                            placeholder="Coordenada X" />
                        
                        <input type="number"
                            style={this.state.style}
                            name="posY"
                            onChange={this.onChange}
                            value={this.state.posX}
                            id="pos-z"
                            placeholder="Coordenada Y" />
                        
                        <input type="number"
                            style={this.state.style}
                            name="posZ"
                            onChange={this.onChange}
                            value={this.state.posX}
                            id="pos-z"
                            placeholder="Coordenada Z" />
                        
                        
                    </div>
                    <Button className="btn-calc"
                        click={this.limpiar}
                        name="Limpiar"></Button>
                </div>
                <div id="calculadora__btn">
                    <Button></Button>
                    <Button></Button>
                </div>
                <div id="calculadora__res"></div>
            </div>
        )
    }
}

export default RestBlocks
