import React, { Component } from 'react';
import './Calculadora.css';
import './Portales.css'
import Boton from './Boton'

class Portales extends Component {

    state = {
        posX: '',
        posY: '',
        posDestX: '',
        posDestY: '',
        style

    }

    errorValues = () => {

    }

    validate = () => {

        




    }

    limpiar = () =>{
        this.setState({
            posX: '',
            posY: ''
        });
    }

    onChange = e => {
        // console.log(e.target.value);
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    overToNeth = () => {
        this.setState({
            posDestX: Math.trunc(parseInt(this.state.posX / 8)) ,
            posDestY: Math.trunc(parseInt(this.state.posY / 8)) ,
            
        })
    }

    nethToOver = () => {
        this.setState({
            posDestX: Math.trunc(parseInt(this.state.posX * 8)),
            posDestY: Math.trunc(parseInt(this.state.posY * 8)),

        })
    }

    render() {
        return (
            <div id="calculadora">
                <h2 id="calculadora__title">Calculadora</h2>
                <div id="calculadora__form">
                    <div id="form__inputs">
                        <input type="number"
                            name="posX"
                            onChange={this.onChange}
                            value={this.state.posX}
                            id="pos-x"
                            placeholder="Coordenada X" />
                        <input type="number" id="pos-y"
                            name="posY"
                            onChange={this.onChange}
                            value={this.state.posY}
                            placeholder="Coordenada Y" />
                    </div>
                    <div id="btn-limpiar">
                        <Boton className="btn-calc" click={this.limpiar} name="Limpiar"></Boton>

                    </div>
                </div>
                <div id="calculadora__btn">
                    <Boton click={this.overToNeth} name="OverWorld  ->  Nether"></Boton>
                    <Boton click={this.nethToOver} name="Nether  ->  OverWorld"></Boton>
                    <Boton click={this.limpiar} name="Guardar"></Boton>
                </div>
                <div id="calculadora__res">
                    <div> X: {this.state.posDestX}</div> | 
                    <div> Y: {this.state.posDestY}</div> | 
                    
                </div>
            </div>
        )
    }
}


const errorNumber = {
    borderBottom: "2px solid rgb(177, 0, 0)",
    borderBottom: "2px solid rgb(177, 0, 0)",
    background: "rgba(177, 146, 146,100)",
    background: "rgba(177, 146, 146,100)"
}

const validNumber = {
    borderBottom: ("2px solid rgb(0, 92, 69)"),
    borderBottom: ("2px solid rgb(0, 92, 69)"),
    background: "transparent",
    background: "transparent"
}



export default Portales;
