import React, { Component } from 'react'
import './Calculadora.css'
import './RestBlocks.css'
import Button from './Boton'

const errorNumber = {
    borderBottom: "2px solid rgb(177, 0, 0)",
    background: "rgba(177, 146, 146,100)"
}
const validNumber = {
    borderBottom: ("2px solid rgb(0, 92, 69)"),
    background: "transparent"
}

export class RestBlocks extends Component {

    state = {
        posX: '',
        posY: '',
        posZ: '',
        posDestX: '',
        posDestY: '',
        posDestZ: '',
        style: validNumber
    }
    validate = (coord) => {
        for (let element of coord) {
            if (isNaN(element)) {
                alert("Ingresa SOLO numeros\n y no modifiques mi html, Gracias\n atte: Rolgus.")
                return false;
            }
            if (element === "") {
                this.errorValues();
                return false;
            }
        }
        return true;
    }

    limpiar = () => {
        this.setState({
            posX: '',
            posY: '',
            posZ: ''
        });
    }
    


    render() {
        return (
            <div id="calculadora">
                <div id="calculadora__form">
                    <div>
                        <input type="number" />
                        <input type="number"/>
                        <input type="number"/>
                    </div>
                    <Boton className="btn-calc"
                        click={this.limpiar} name="Limpiar"></Boton>
                </div>
                <div id="calculadora__btn">
                    <Button></Button>
                    <Button></Button>
                </div>
                <div id="calculadora__res"></div>
            </div>
        )
    }
}

export default RestBlocks
